package lmkj.freetouch;import android.content.Context;public abstract class DiskButtonInfo {    public int index;    public int drawableId;    public String text;    public boolean isFront;    public boolean isRemove = false;    public boolean canDelete = true;    public DiskButtonInfo(int index, int drawableId, String text, boolean front) {        this(index, drawableId, text, front, false, true);    }    public DiskButtonInfo(int index, int drawableId, String text, boolean front, boolean remove, boolean del) {        this.index = index;        this.drawableId = drawableId;        this.text = text;        this.isFront = front;        this.isRemove = remove;        this.canDelete = del;    }    public void remove() {        drawableId = R.drawable.ic_add;        text = "";        isRemove = true;        canDelete = false;    }    public abstract void execute(Context context);    public abstract void updateIcon();}