package lmkj.freetouch;import android.content.Context;public abstract class DiskButtonInfo {    int index;    public int drawableId;    public String text;    boolean isFront;    boolean isRemove = false;    boolean canDelete = true;    public DiskButtonInfo(int index, int drawableId, String text, boolean front) {        this.index = index;        this.drawableId = drawableId;        this.text = text;        this.isFront = front;    }    public void remove() {        drawableId = R.drawable.ic_add;        text = "";        isRemove = true;        canDelete = false;    }    public abstract void execute(Context context);    public abstract void updateIcon();}